INSERT INTO `v9_news` VALUES ('16', '26', '0', '关于开展“中央企业故事大赛”征稿活动的公告', '', '', '中央 公告 故事', '为全面回顾改革开放40年来波澜壮阔的伟大历程，生动展现中央企业改革开放以来特别是十八大以来取得的辉煌成就，国资委宣传局决定在中央企业开展“走进新时代展现新形象—中国石化长城', '0', 'http://localhost/phpcms_v9_UTF8/install_package/index.php?m=content&c=index&a=show&catid=26&id=16', '0', '99', '1', '0', 'phpcms', '1536633720', '1539225782'), ('15', '26', '0', '华润集团第一届董事会第十四次会议决议公告', '', '', '华润集团 决议 董事会', '本公司董事会及全体董事保证本公告内容不存在任何虚假记载、误导性陈述或者重大遗漏，并对其内容的真实性、准确性和完整性承担责任。', '0', 'http://localhost/phpcms_v9_UTF8/install_package/index.php?m=content&c=index&a=show&catid=26&id=15', '0', '99', '1', '0', 'phpcms', '1537410900', '1539225495'), ('14', '23', '0', '红安华润希望小镇项目正式启动', '', 'W020181003341904867478.jpg', '华润 红安 小镇', '9月28日，红安华润希望小镇正式启动。集团傅育宁董事长、王传栋副总经理等集团领导，以及湖北黄冈市市长邱丽新、红安县委书记余学武等市县领导出席了启动仪式，并实地考察了红安华润希', '0', 'http://localhost/phpcms_v9_UTF8/install_package/index.php?m=content&c=index&a=show&catid=23&id=14', '0', '99', '1', '0', 'phpcms', '1538447460', '1539225189'), ('12', '25', '0', '傅育宁董事长与福建省于伟国书记会谈', '', 'W020181010534883782009.jpg', '伟国 福建省 董事长', '10月9日上午，在香港访问的福建省委书记、省人大常委会主任于伟国率团走访了香港中国企业协会，集团傅育宁董事长作为香港中企协会名誉会长代表中企协在香港华润大厦与于伟国书记进行了', '0', 'http://localhost/phpcms_v9_UTF8/install_package/index.php?m=content&c=index&a=show&catid=25&id=12', '0', '99', '1', '0', 'phpcms', '1539221868', '1539222007'), ('13', '22', '0', '傅育宁董事长拜会湖北省领导并调研', '', 'W020180929421160238960.jpg', '湖北省 董事长 傅育宁', '9月27日，傅育宁董事长在武汉拜会了湖北省委书记、省人大常委会主任蒋超良，省委副书记、省长王晓东，就深入加强多领域合作进行亲切座谈，达成广泛共识。湖北省委副书记、武汉市委书记', '0', 'http://localhost/phpcms_v9_UTF8/install_package/index.php?m=content&c=index&a=show&catid=22&id=13', '0', '99', '1', '0', 'phpcms', '1538187840', '1539224795'), ('19', '72', '0', '华润集团企业简介片', '', 'http://localhost/phpcms_v9_UTF8/install_package/uploadfile/2018/1016/20181016081810711.jpg', '华润集团 企业简介', '华润集团简介', '0', 'http://localhost/phpcms_v9_UTF8/install_package/index.php?m=content&c=index&a=show&catid=72&id=19', '0', '99', '1', '0', 'phpcms', '1539692172', '1539692468'), ('18', '73', '0', '雪花纯生形象篇', '', 'http://localhost/phpcms_v9_UTF8/install_package/uploadfile/2018/1016/20181016092744984.jpg', '雪花', '雪花纯生形象篇', '0', 'http://localhost/phpcms_v9_UTF8/install_package/index.php?m=content&c=index&a=show&catid=73&id=18', '0', '99', '1', '0', 'phpcms', '1539652962', '1539653281'), ('20', '72', '0', '华润集团形象篇', '', 'http://localhost/phpcms_v9_UTF8/install_package/uploadfile/2018/1016/20181016082055313.jpg', '华润集团 形象', '华润集团形象篇', '0', 'http://localhost/phpcms_v9_UTF8/install_package/index.php?m=content&c=index&a=show&catid=72&id=20', '0', '99', '1', '0', 'phpcms', '1539692358', '1539692478'), ('21', '26', '0', '华润集团第一届董事会第十三次会议决议公告', '', '', '华润集团 决议 董事会', '公司董事会于2018年7月25日在惠州小径湾华润大学召开会议。会议应出席董事7人，实际出席董事7人。公司管理层列席会议。会议符合公司法、公司章程和董事会议事规则的相关规定，会议合法', '0', 'http://localhost/phpcms_v9_UTF8/install_package/index.php?m=content&c=index&a=show&catid=26&id=21', '0', '99', '1', '0', 'phpcms', '1532695500', '1539694000');
